INSERT INTO `biz_project_progress_query_rule_config` VALUES ('1', null, '1', 'SELECT a.id AS \'order_id\', so.`name` AS \'store_name\', en.`name` AS \'engin_depart_name\', a.order_number, a.area, DATE_FORMAT( a.get_order_datetime, \'%Y-%m-%d\' ) AS \'get_order_datetime\', a.customer_name, a.customer_phone, concat_ws( \'-\', a.community_name, a.build_number, a.build_unit, a.build_room ) AS \'customer_address\', a.contract_area, sd.label AS \'build_type\', a.item_manager, em.phone AS \'item_manager_phone\', a.designer_name, a.designer_phone, a.order_inspector, em2.phone AS \'order_inspector_phone\', DATE_FORMAT(a.contract_start_date, \'%Y-%m-%d\') AS \'contract_start_date\', DATE_FORMAT(a.contract_end_date, \'%Y-%m-%d\' )AS \'contract_end_date\', DATE_FORMAT(a.actual_start_date, \'%Y-%m-%d\' )AS \'actual_start_date\', DATE_FORMAT( a.actual_end_date, \'%Y-%m-%d\' ) AS \'actual_end_date\', TIMESTAMPDIFF( DAY, a.contract_start_date, a.actual_start_date ) AS \'start_diff_day\' FROM biz_order a LEFT JOIN sys_office so ON so.id = a.store_id LEFT JOIN biz_engineering_department en ON en.id = a.engin_depart_id LEFT JOIN biz_employee em ON em.id = a.item_manager_id LEFT JOIN biz_employee em2 ON em2.id = a.order_inspector_id LEFT JOIN sys_dict sd ON sd.type = \'emp_house_type\' AND sd.del_flag = 0 AND sd.`value` = a.build_type', 'order_basic_info', '订单基本信息', '1', null, null, '1', '2017-12-13 14:50:57', '1', '2017-12-26 11:44:57', '0');
