INSERT INTO `biz_project_progress_query_rule_config` VALUES ('6', null, '6', 'SELECT DATE_FORMAT( b.actual_start_date, \'%Y-%m-%d\' ) AS \'security_door_measure_plan_submit_date\', a.create_date AS \'security_door_measure_actual_submit_time\', TIMESTAMPDIFF( DAY, b.actual_start_date, a.create_date ) AS \'security_door_measure_submit_dealy_days\', DATE_FORMAT( a.checksize_date, \'%Y-%m-%d\' ) AS \'security_door_measure_expect_arrival_date\', ( SELECT DATE_FORMAT( p.plan_done_date, \'%Y-%m-%d\' ) FROM biz_node_plan p WHERE p.order_id = b.id AND p.node_index = 1 ORDER BY p.create_date DESC LIMIT 1 ) AS \'security_door_measure_plan_complete_date\', a.supplier_confirm_date AS \'security_door_measure_actual_complete_time\', TIMESTAMPDIFF( DAY, ( SELECT p.plan_done_date FROM biz_node_plan p WHERE p.order_id = b.id AND p.node_index = 1 ORDER BY p.create_date DESC LIMIT 1 ), a.supplier_confirm_date ) AS \'security_door_measure_complete_delay_days\' FROM biz_order b LEFT JOIN biz_order_checksize a ON a.order_id = b.id AND a.checksize_type = \'1\' WHERE b.id = :orderId ORDER BY a.create_date DESC LIMIT 1', 'security_door_measure', '防盗门核尺', '0', null, null, '1', '2017-12-13 15:18:47', '1', '2017-12-21 16:02:06', '0');
INSERT INTO `biz_project_progress_query_rule_config` VALUES ('7', null, '7', 'SELECT DATE_FORMAT( DATE_ADD( b.actual_start_date, INTERVAL ( IFNULL(c.Late_completion_days, 0) - 3 ) DAY ), \'%Y-%m-%d\' ) AS \'auxiliary_materials_arrival_plan_submit_date\', a.apply_time AS \'auxiliary_materials_arrival_actual_submit_time\', TIMESTAMPDIFF( DAY, ( DATE_ADD( b.actual_start_date, INTERVAL ( IFNULL(c.Late_completion_days, 0) - 3 ) DAY )), DATE_FORMAT(a.apply_time,\'%Y-%m-%d\') ) AS \'auxiliary_materials_arrival_submit_delay_days\', DATE_FORMAT( a.apply_receive_time, \'%Y-%m-%d\' ) AS \'auxiliary_materials_arrival_expect_arrival_date\', DATE_FORMAT(( SELECT p.plan_done_date FROM biz_node_plan p WHERE p.order_id = b.id AND p.node_index = 2 ORDER BY p.create_date DESC LIMIT 1 ), \'%Y-%m-%d\' ) AS \'auxiliary_materials_arrival_plan_complete_date\', ( SELECT b.receive_date FROM biz_purchase_receive_bill b WHERE b.purchase_id = a.id ORDER BY b.receive_date ASC LIMIT 1 ) AS \'auxiliary_materials_arrival_actual_receive_time\', TIMESTAMPDIFF( DAY, ( SELECT p.plan_done_date FROM biz_node_plan p WHERE p.order_id = b.id AND p.node_index = 2 ORDER BY p.create_date DESC LIMIT 1 ), ( SELECT b.receive_date FROM biz_purchase_receive_bill b WHERE b.purchase_id = a.id ORDER BY b.receive_date ASC LIMIT 1 )) AS \'auxiliary_materials_arrival_receive_delay_days\' FROM biz_order b LEFT JOIN biz_purchase a ON a.order_id = b.id AND a.purchase_type = \'1\' AND a. STATUS != \'21\' LEFT JOIN biz_construction_schedule c ON c.store_id = b.store_id AND c.project_mode = b.project_mode AND c.is_old_house = b.house_is_new AND is_enable = 1 AND c.sort = 2 WHERE b.id = :orderId ORDER BY a.create_date ASC LIMIT 1', 'auxiliary_materials_arrival', '辅材进场', '0', null, null, '1', '2017-12-13 15:19:26', '1', '2018-01-03 15:02:27', '0');
INSERT INTO `biz_project_progress_query_rule_config` VALUES ('8', null, '8', 'SELECT DATE_FORMAT( DATE_ADD( b.actual_start_date, INTERVAL ( IFNULL(c.Late_completion_days, 0) - 3 ) DAY ), \'%Y-%m-%d\' ) AS \'ceramic_tile_plan_submit_date\', a.apply_time AS \'ceramic_tile_actual_submit_time\', TIMESTAMPDIFF( DAY, ( DATE_ADD( b.actual_start_date, INTERVAL ( IFNULL(c.Late_completion_days, 0) - 3 ) DAY )), DATE_FORMAT(a.apply_time,\'%Y-%m-%d\') ) AS \'ceramic_tile_submit_delay_days\', DATE_FORMAT( a.apply_receive_time, \'%Y-%m-%d\' ) AS \'ceramic_tile_expect_arrival_date\', ( SELECT n.plan_done_date FROM biz_node_plan n WHERE n.order_id = b.id AND n.node_index = 3 ORDER BY n.create_date DESC LIMIT 1 ) AS \'ceramic_tile_plan_complete_date\', ( SELECT b.receive_date FROM biz_purchase_receive_bill b WHERE b.purchase_id = a.id ORDER BY b.receive_date ASC LIMIT 1 ) AS \'ceramic_tile_actual_receive_time\', TIMESTAMPDIFF( DAY, ( SELECT n.plan_done_date FROM biz_node_plan n WHERE n.order_id = b.id AND n.node_index = 3 ORDER BY n.create_date DESC LIMIT 1 ), ( SELECT b.receive_date FROM biz_purchase_receive_bill b WHERE b.purchase_id = a.id ORDER BY b.receive_date ASC LIMIT 1 )) AS \'ceramic_tile_receive_delay_days\' FROM biz_order b LEFT JOIN biz_purchase a ON a.order_id = b.id AND a.purchase_type = \'5\' AND a. STATUS != \'21\' LEFT JOIN biz_construction_schedule c ON c.store_id = b.store_id AND c.project_mode = b.project_mode AND c.is_old_house = b.house_is_new AND is_enable = 1 AND c.sort = 3 WHERE b.id = :orderId ORDER BY a.create_date ASC LIMIT 1', 'ceramic_tile', '瓷砖', '0', null, null, '1', '2017-12-22 10:24:10', '1', '2017-12-27 17:48:05', '0');
INSERT INTO `biz_project_progress_query_rule_config` VALUES ('9', null, '9', 'SELECT DATE_FORMAT( DATE_ADD( c.actual_start_date, INTERVAL (IFNULL(b.days_to_apply, 0)) DAY ), \'%Y-%m-%d\' ) AS \'waterway_circuit_concealment_plan_submit_date\', a.create_date AS \'waterway_circuit_concealment_actual_submit_time\', TIMESTAMPDIFF( DAY, DATE_ADD( c.actual_start_date, INTERVAL (IFNULL(b.days_to_apply, 0)) DAY ), DATE_FORMAT(a.create_date,\'%Y-%m-%d\') ) AS \'waterway_circuit_concealment_submit_delay_days\', DATE_FORMAT( a.expect_check_datetime, \'%Y-%m-%d\' ) AS \'waterway_circuit_concealment_expect_arrival_date\', DATE_FORMAT(d.sign_datetime, \'%Y-%m-%d\') AS \'waterway_circuit_concealment_actual_arrival_time\', TIMESTAMPDIFF( DAY, DATE_FORMAT( a.expect_check_datetime, \'%Y-%m-%d\' ), DATE_FORMAT(d.sign_datetime, \'%Y-%m-%d\')) AS \'waterway_circuit_concealment_arrival_delay_days\', ( SELECT n.plan_done_date FROM biz_node_plan n WHERE n.order_id = c.id AND n.node_index = 4 ORDER BY n.create_date DESC LIMIT 1 ) AS \'waterway_circuit_concealment_plan_complete_date\', ( SELECT DATE_FORMAT( a.accept_check_datetime, \'%Y-%m-%d\' ) FROM biz_qc_bill a INNER JOIN biz_qc_check_node b ON a.qc_check_node_id = b.id WHERE a.qc_bill_type = \'1\' AND a.is_recheck = \'0\' AND a. STATUS &gt;= \'10\' AND a.order_id = c.id AND b.qc_check_node_index = 1 ORDER BY a.create_date DESC LIMIT 1 ) AS \'waterway_circuit_concealment_actual_receive_time\', TIMESTAMPDIFF( DAY, ( SELECT n.plan_done_date FROM biz_node_plan n WHERE n.order_id = c.id AND n.node_index = 4 ORDER BY n.create_date DESC LIMIT 1 ), ( SELECT DATE_FORMAT( a.accept_check_datetime, \'%Y-%m-%d\' ) FROM biz_qc_bill a INNER JOIN biz_qc_check_node b ON a.qc_check_node_id = b.id WHERE a.qc_bill_type = \'1\' AND a.is_recheck = \'0\' AND a.STATUS &gt;= \'10\' AND a.order_id = c.id AND b.qc_check_node_index = 1 ORDER BY a.create_date DESC LIMIT 1 )) AS \'waterway_circuit_concealment_receive_delay_days\' FROM biz_order c LEFT JOIN biz_qc_bill a ON a.order_id = c.id LEFT JOIN biz_qc_check_node b ON a.qc_check_node_id = b.id LEFT JOIN biz_business_sign d ON d.related_business_id_int = a.id AND d.sign_type = 301 WHERE a.qc_bill_type = \'1\' AND a.is_recheck = \'0\' AND c.id = :orderId AND b.qc_check_node_index = 1 ORDER BY a.create_date DESC LIMIT 1', 'waterway_circuit_concealment', '水电隐蔽验收', '0', null, null, '1', '2017-12-22 10:23:23', '1', '2018-01-03 15:00:19', '0');
INSERT INTO `biz_project_progress_query_rule_config` VALUES ('10', null, '10', 'SELECT DATE_FORMAT( DATE_ADD( c.actual_start_date, INTERVAL (IFNULL(b.days_to_apply, 0)) DAY ), \'%Y-%m-%d\' ) AS \'waterproof_accept_plan_submit_date\', a.create_date AS \'waterproof_accept_actual_submit_time\', TIMESTAMPDIFF( DAY, DATE_ADD( c.actual_start_date, INTERVAL (IFNULL(b.days_to_apply, 0)) DAY ), DATE_FORMAT(a.create_date,\'%Y-%m-%d\') ) AS \'waterproof_accept_submit_delay_days\', DATE_FORMAT( a.expect_check_datetime, \'%Y-%m-%d\' ) AS \'waterproof_accept_expect_arrival_date\', DATE_FORMAT(d.sign_datetime, \'%Y-%m-%d\') AS \'waterproof_accept_actual_arrival_time\', TIMESTAMPDIFF( DAY, DATE_FORMAT( a.expect_check_datetime, \'%Y-%m-%d\' ), DATE_FORMAT(d.sign_datetime, \'%Y-%m-%d\')) AS \'waterproof_accept_arrival_delay_days\', ( SELECT n.plan_done_date FROM biz_node_plan n WHERE n.order_id = c.id AND n.node_index = 5 ORDER BY n.create_date DESC LIMIT 1 ) AS \'waterproof_accept_plan_complete_date\', ( SELECT DATE_FORMAT( a.accept_check_datetime, \'%Y-%m-%d\' ) FROM biz_qc_bill a INNER JOIN biz_qc_check_node b ON a.qc_check_node_id = b.id WHERE a.qc_bill_type = \'1\' AND a.is_recheck = \'0\' AND a. STATUS &gt;= \'10\' AND a.order_id = c.id AND b.qc_check_node_index = 2 ORDER BY a.create_date DESC LIMIT 1 ) AS \'waterproof_accept_actual_receive_time\', TIMESTAMPDIFF( DAY, ( SELECT n.plan_done_date FROM biz_node_plan n WHERE n.order_id = c.id AND n.node_index = 5 ORDER BY n.create_date DESC LIMIT 1 ), ( SELECT DATE_FORMAT( a.accept_check_datetime, \'%Y-%m-%d\' ) FROM biz_qc_bill a INNER JOIN biz_qc_check_node b ON a.qc_check_node_id = b.id WHERE a.qc_bill_type = \'1\' AND a.is_recheck = \'0\' AND a. STATUS &gt;= \'10\' AND a.order_id = c.id AND b.qc_check_node_index = 2 ORDER BY a.create_date DESC LIMIT 1 )) AS \'waterproof_accept_receive_delay_days\' FROM biz_order c LEFT JOIN biz_qc_bill a ON a.order_id = c.id LEFT JOIN biz_qc_check_node b ON a.qc_check_node_id = b.id LEFT JOIN bi