INSERT INTO `biz_project_progress_query_rule_config` VALUES ('11', null, '11', 'SELECT DATE_FORMAT( DATE_ADD( c.actual_start_date, INTERVAL (IFNULL(b.days_to_apply, 0)) DAY ), \'%Y-%m-%d\' ) AS \'bricklayer_accept_plan_submit_date\', a.create_date AS \'bricklayer_accept_actual_submit_time\', TIMESTAMPDIFF( DAY, DATE_ADD( c.actual_start_date, INTERVAL (IFNULL(b.days_to_apply, 0)) DAY ), DATE_FORMAT(a.create_date,\'%Y-%m-%d\') ) AS \'bricklayer_accept_submit_delay_days\', DATE_FORMAT( a.expect_check_datetime, \'%Y-%m-%d\' ) AS \'bricklayer_accept_expect_arrival_date\', DATE_FORMAT(d.sign_datetime, \'%Y-%m-%d\') AS \'bricklayer_accept_actual_arrival_time\', TIMESTAMPDIFF( DAY, DATE_FORMAT( a.expect_check_datetime, \'%Y-%m-%d\' ), DATE_FORMAT(d.sign_datetime, \'%Y-%m-%d\')) AS \'bricklayer_accept_arrival_delay_days\', ( SELECT n.plan_done_date FROM biz_node_plan n WHERE n.order_id = c.id AND n.node_index = 7 ORDER BY n.create_date DESC LIMIT 1 ) AS \'bricklayer_accept_plan_complete_date\', ( SELECT DATE_FORMAT( a.accept_check_datetime, \'%Y-%m-%d\' ) FROM biz_qc_bill a INNER JOIN biz_qc_check_node b ON a.qc_check_node_id = b.id WHERE a.qc_bill_type = \'1\' AND a.is_recheck = \'0\' AND a. STATUS &gt;= \'10\' AND a.order_id = c.id AND b.qc_check_node_index = 3 ORDER BY a.create_date DESC LIMIT 1 ) AS \'bricklayer_accept_actual_receive_time\', TIMESTAMPDIFF( DAY, ( SELECT n.plan_done_date FROM biz_node_plan n WHERE n.order_id = c.id AND n.node_index = 7 ORDER BY n.create_date DESC LIMIT 1 ), ( SELECT DATE_FORMAT( a.accept_check_datetime, \'%Y-%m-%d\' ) FROM biz_qc_bill a INNER JOIN biz_qc_check_node b ON a.qc_check_node_id = b.id WHERE a.qc_bill_type = \'1\' AND a.is_recheck = \'0\' AND a. STATUS &gt;= \'10\' AND a.order_id = c.id AND b.qc_check_node_index = 3 ORDER BY a.create_date DESC LIMIT 1 )) AS \'bricklayer_accept_receive_delay_days\' FROM biz_order c LEFT JOIN biz_qc_bill a ON a.order_id = c.id LEFT JOIN biz_qc_check_node b ON a.qc_check_node_id = b.id LEFT JOIN biz_business_sign d ON d.related_business_id_int = a.id AND d.sign_type = 301 WHERE a.qc_bill_type = \'1\' AND a.is_recheck = \'0\' AND c.id = :orderId AND b.qc_check_node_index = 3 ORDER BY a.create_date DESC LIMIT 1', 'bricklayer_accept', '瓦工验收', '0', null, null, '1', '2017-12-22 10:24:50', '1', '2017-12-27 17:56:39', '0');
INSERT INTO `biz_project_progress_query_rule_config` VALUES ('12', null, '12', 'SELECT ( SELECT n.plan_done_date FROM biz_node_plan n WHERE n.order_id = a.order_id AND n.node_index = 8 ORDER BY n.create_date DESC LIMIT 1 ) AS \'two_period_plan_submit_date\', DATE_FORMAT( a.accept_check_datetime, \'%Y-%m-%d\' ) AS \'two_period_urge_date\', ( SELECT DATE_FORMAT( f.receive_money_datetime, \'%Y-%m-%d\' ) FROM biz_pre_pm_settle_finance_receive_moeny f WHERE f.receive_money_type = \'1\' AND f.del_flag = \'0\' AND f.order_id = a.order_id ORDER BY f.create_date DESC LIMIT 1 ) AS \'two_period_actual_submit_time\', TIMESTAMPDIFF( DAY, ( SELECT n.plan_done_date FROM biz_node_plan n WHERE n.order_id = a.order_id AND n.node_index = 8 ORDER BY n.create_date DESC LIMIT 1 ), ( SELECT DATE_FORMAT( f.receive_money_datetime, \'%Y-%m-%d\' ) FROM biz_pre_pm_settle_finance_receive_moeny f WHERE f.receive_money_type = \'1\' AND f.del_flag = \'0\' AND f.order_id = a.order_id ORDER BY f.create_date DESC LIMIT 1 )) AS \'two_period_delay_days\' FROM biz_order c LEFT JOIN biz_qc_bill a ON a.order_id = c.id LEFT JOIN biz_qc_check_node b ON a.qc_check_node_id = b.id WHERE a.qc_bill_type = \'1\' AND a.is_recheck = \'0\' AND a. STATUS &gt;= \'10\' AND c.id = :orderId AND b.qc_check_node_index = 3 ORDER BY a.create_date DESC LIMIT 1', 'two_period', '二期款', '0', null, null, '1', '2017-12-22 10:26:04', '1', '2017-12-22 11:51:35', '0');
INSERT INTO `biz_project_progress_query_rule_config` VALUES ('13', null, '13', 'SELECT DATE_FORMAT( DATE_ADD( c.actual_start_date, INTERVAL (IFNULL(b.days_to_apply, 0)) DAY ), \'%Y-%m-%d\' ) AS \'base_construct_accept_plan_submit_date\', a.create_date AS \'base_construct_accept_actual_submit_time\', TIMESTAMPDIFF( DAY, DATE_ADD( c.actual_start_date, INTERVAL (IFNULL(b.days_to_apply, 0)) DAY ), DATE_FORMAT(a.create_date,\'%Y-%m-%d\') ) AS \'base_construct_accept_submit_delay_days\', DATE_FORMAT( a.expect_check_datetime, \'%Y-%m-%d\' ) AS \'base_construct_accept_expect_arrival_date\', DATE_FORMAT(d.sign_datetime, \'%Y-%m-%d\') AS \'base_construct_accept_actual_arrival_time\', TIMESTAMPDIFF( DAY, DATE_FORMAT( a.expect_check_datetime, \'%Y-%m-%d\' ), DATE_FORMAT(d.sign_datetime, \'%Y-%m-%d\')) AS \'base_construct_accept_arrival_delay_days\', ( SELECT n.plan_done_date FROM biz_node_plan n WHERE n.order_id = c.id AND n.node_index = 9 ORDER BY n.create_date DESC LIMIT 1 ) AS \'base_construct_accept_plan_complete_date\', ( SELECT DATE_FORMAT( a.accept_check_datetime, \'%Y-%m-%d\' ) FROM biz_qc_bill a INNER JOIN biz_qc_check_node b ON a.qc_check_node_id = b.id WHERE a.qc_bill_type = \'1\' AND a.is_recheck = \'0\' AND a. STATUS &gt;= \'10\' AND a.order_id = c.id AND b.qc_check_node_index = 6 ORDER BY a.create_date DESC LIMIT 1 ) AS \'base_construct_accept_actual_receive_time\', TIMESTAMPDIFF( DAY, ( SELECT n.plan_done_date FROM biz_node_plan n WHERE n.order_id = c.id AND n.node_index = 9 ORDER BY n.create_date DESC LIMIT 1 ), ( SELECT DATE_FORMAT( a.accept_check_datetime, \'%Y-%m-%d\' ) FROM biz_qc_bill a INNER JOIN biz_qc_check_node b ON a.qc_check_node_id = b.id WHERE a.qc_bill_type = \'1\' AND a.is_recheck = \'0\' AND a. STATUS &gt;= \'10\' AND a.order_id = c.id AND b.qc_check_node_index = 6 ORDER BY a.create_date DESC LIMIT 1 )) AS \'base_construct_accept_receive_delay_days\' FROM biz_order c LEFT JOIN biz_qc_bill a ON a.order_id = c.id LEFT JOIN biz_qc_check_node b ON a.qc_check_node_id = b.id LEFT JOIN biz_business_sign d ON d.related_business_id_int = a.id AND d.sign_type = 301 WHERE a.qc_bill_type = \'1\' AND a.is_recheck = \'0\' AND c.id = :orderId AND b.qc_check_node_index = 6 ORDER BY a.create_date DESC LIMIT 1', 'base_construct_accept', '基础施工验收', '0', null, null, '1', '2017-12-22 10:26:25', '1', '2017-12-27 17:23:50', '0');
