mapper.select:
SELECT
FROM biz_order_distribute_log log LEFT JOIN sys_user u ON u.id = log.create_by LEFT JOIN biz_employee ep ON ep.id = u.emp_id LEFT JOIN biz_employee emp1 ON emp1.id = log.distributed_employee_id LEFT JOIN biz_employee emp2 ON emp2.id = log.old_employee_id LEFT JOIN biz_order o ON o.id = log.order_id LEFT JOIN biz_engineering_department d ON d.id = o.engin_depart_id AND d.del_flag = 0 LEFT JOIN sys_dict dict ON dict.`value` = o.project_mode AND dict.type = 'project_mode' AND dict.del_flag = 0 LEFT JOIN biz_employee emp3 ON emp3.id = o.item_manager_id LEFT JOIN biz_employee emp4 ON emp4.id = o.order_inspector_id LEFT JOIN sys_office f ON f.id = o.store_id
log.distribute_type in (101,102)
AND o.store_id = #{storeId}
AND o.project_mode = #{projectMode}
AND o.engin_depart_id =#{engineDepartId}
AND o.order_number LIKE CONCAT('%','${orderNumber}','%' )
AND o.customer_name LIKE '%'||#{customerName}||'%' '%'+#{customerName}+'%' concat('%',#{customerName},'%')
AND emp3.realName LIKE '%'||#{itemManager}||'%' '%'+#{itemManager}+'%' concat('%',#{itemManager},'%')
AND log.create_date <= #{endCreateDate}
AND log.create_date >= #{beginCreateDate}
ORDER BY log.create_date DESC

SELECT
FROM biz_employee a LEFT JOIN sys_office bb ON bb.id = a.storeId LEFT JOIN sys_dict c ON c.`value` = a.project_mode AND c.type = 'project_mode' AND c.del_flag = 0 LEFT JOIN biz_engin_depart_employee_position b1 ON a.id = b1.employee_id AND b1.position_type = '5' LEFT JOIN biz_engineering_department c1 ON b1.engin_depart_id = c1.id LEFT JOIN ( SELECT mm.distributed_employee_id, COUNT(1) AS "count", mm.create_date FROM biz_order_distribute_log mm, ( SELECT MAX(b.id) AS "logId" FROM biz_order_distribute_log b WHERE b.distribute_type IN (101, 102) AND b.create_date < ( SELECT IFNULL(max(h.create_date),'2099-12-31') FROM biz_order_confirm_startwork h WHERE h.order_id = b.order_id ) GROUP BY b.order_id ) www WHERE mm.id = www.logId
AND mm.create_date <= #{endCreateDate}
AND mm.create_date >= #{beginCreateDate}
GROUP BY mm.distributed_employee_id ) q ON q.distributed_employee_id = a.id LEFT JOIN ( SELECT d.item_manager_id, d.id, d.customer_name, COUNT(1) AS "orderCount" FROM biz_order d GROUP BY d.item_manager_id ) m ON m.item_manager_id = a.id
a.empType = 3 AND a.del_flag = 0
AND a.storeId = #{storeId}
AND a.project_mode = #{projectMode}
AND c1.id =#{engineDepartId}
AND m.id LIKE CONCAT('%','${orderNumber}','%' )
AND m.customer_name LIKE '%'||#{customerName}||'%' '%'+#{customerName}+'%' concat('%',#{customerName},'%')
AND a.realName LIKE '%'||#{itemManager}||'%' '%'+#{itemManager}+'%' concat('%',#{itemManager},'%')
ORDER BY a.storeId, a.project_mode, c1.id, CONVERT(a.realName using gbk)
