mapper.select:
SELECT a.id, a.community_name, a.build_number, a.build_unit, a.build_room, a.item_manager_id, c.realName AS "itemManager", c.phone AS "itemManagerPhone", a.order_inspector_id, d.realName AS "orderInspector", d.phone AS "orderInspectorPhone", b.id AS "qcBillId", b.qc_bill_type, b.is_recheck, b.qc_check_node_id, b.check_datetime, e.qc_check_node_name, (SELECT COUNT(1) FROM biz_business_view_log g WHERE g.business_id_int = b.id AND g.business_type = '3') AS "viewCount" FROM biz_order a LEFT JOIN biz_qc_bill b ON a.id = b.order_id AND b.qc_bill_type = '1' AND b.`status` > 2 LEFT JOIN biz_employee c ON c.id = a.item_manager_id LEFT JOIN biz_employee d ON d.id = a.order_inspector_id LEFT JOIN biz_qc_check_node e ON e.id = b.qc_check_node_id
WHERE a.id = ( SELECT h.id FROM biz_order h WHERE h.customer_phone = #{customerPhone}
AND h.id = #{orderId}
ORDER BY
( SELECT COUNT(1) FROM biz_qc_bill w WHERE h.id = w.order_id AND w.qc_bill_type = '1' AND w.`status` > 2 AND w.id NOT IN ( SELECT n.business_id_int FROM biz_business_view_log n WHERE n.business_id_int = w.id AND n.business_type = '3' ) ) DESC,h.create_date ASC LIMIT 1 ) ORDER BY a.create_date ASC, viewCount ASC, b.qc_check_node_id ASC, b.check_datetime ASC

SELECT
	COUNT(1)
FROM
	biz_business_view_log a
WHERE
	a.business_id_int = #{businessIdInt}
AND a.business_type = #{businessType}
AND a.business_viewer_only_mark = #{businessViewerOnlyMark}
